        [HttpPost]
        public IHttpActionResult SaveChanges(Models.Urun model)
        {
            if (model.ModelState == EntityState.Deleted)
            {
                if (!Urunler.ContainsKey(model.Id)) return NotFound();

                Urunler.Remove(model.Id);
                return Ok();
            }

            model.ModelState = EntityState.Unchanged;
            if (Urunler.ContainsKey(model.Id))
            {
                Urunler[model.Id] = model;
            }
            else
            {
                model.Id = (Urunler.Count > 0 ? Urunler.Keys.Max() : 0) + 1;
                Urunler.Add(model.Id, model);
            }

            if (model.EkliDosyalar != null && model.EkliDosyalar.Any())
            {
                model.EkliDosyalar.ForEach(item =>
                {
                    if (string.IsNullOrEmpty(item.CacheKey)) return;
                    if (!UploadedFiles.ContainsKey(item.CacheKey)) return;

                    var file = UploadedFiles[item.CacheKey];
                    //save file to storage
                    item.CacheKey = "";
                    item.Id = UploadedFiles.Count + 1;
                });
            }

            return Ok(new { entity = model });
        }

        [HttpGet]
        public IHttpActionResult GetList([FromUri]UrunFilter filter)
        {
            var list = (from row in Urunler.Values
                        where (!filter.KategoriId.HasValue || row.KategoriId == filter.KategoriId) &&
                              (filter.Kodu == null || row.Kodu == filter.Kodu) &&
                              (filter.UrunAdi == null || row.UrunAdi.StartsWith(filter.UrunAdi))
                        select new
                        {
                            row.Kodu,
                            row.Id,
                            row.UrunAdi,
                            row.KategoriId,
                            KategoriController.Kategori[row.KategoriId].KategoriAdi,
                            row.BirimFiyat,
                            row.UrunResmi,
                            row.StokMiktari
                        }
                        ).ToList();
            return Ok(list);
        }

        [HttpGet]
        public IHttpActionResult GetModelById(int id)
        {
            return Ok(Urunler[id]);
        }

        [HttpPost]
        public async Task<IHttpActionResult> UploadFile()
        {
            if (!Request.Content.IsMimeMultipartContent())
                throw new HttpResponseException(HttpStatusCode.UnsupportedMediaType);

            var provider = new MultipartMemoryStreamProvider();
            await Request.Content.ReadAsMultipartAsync(provider);

            try
            {
                var cacheKey = Guid.NewGuid().ToString();
                foreach (var file in provider.Contents)
                {
                    var filename = file.Headers.ContentDisposition.FileName.Trim('\"');
                    var buffer = await file.ReadAsByteArrayAsync();
                    UploadedFiles.Add(cacheKey, new DemoFile { FileContent = buffer, Name = filename });
                }
                return Ok(new { newUid = cacheKey });
            }
            catch (Exception exception)
            {
                return BadRequest(exception.Message);
            }
        }
